mapper.select:

	
		
			and id = #{params.id}
		
		
			and version_code = #{params.versionCode}
		
		
			and version_name = #{params.versionName}
		
		
			and remark = #{params.remark}
		
		
			and url = #{params.url}
		
		
			and is_force = #{params.isForce}
		
		
			and client_type = #{params.clientType}
		
	
ORDER BY id DESC

SELECT  id AS id, version_code AS versionCode, version_name AS versionName, remark AS remark, url AS url, is_force AS isForce, client_type AS clientType FROM platform_app_version_nb

		
			and id = #{id}
		
		
			and version_code = #{versionCode}
		
		
			and version_name = #{versionName}
		
		
			and remark = #{remark}
		
		
			and url = #{url}
		
		
			and is_force = #{isForce}
		
		
			and client_type = #{clientType}
		
	
ORDER BY id DESC
